// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

import "./BaseERC1155.sol";

/*
 *  Bottle - This is a ERC1155 contract that mints bottles.
 */
contract Bottle is BaseERC1155
{
  using SafeMath for uint256;

  /*
   *  Batch is a batch of bottles minted.
   */
  struct Batch {
    // Does the bottle exist?
    bool exists;

    // Cost in ETH per mint.
    uint256 ethCost;

    // Number of bottles in the batch
    uint256 maxBottles;
    uint256 numBottles;
  }

  uint256 public batchCount;
  mapping (uint256 => Batch) internal batches;

  //////////////////////////////////////////////////////////////////////////////


  /*
   *  Constructor!
   */
  constructor(
    string memory _uri,
    address _proxyRegistryAddress
  )
    BaseERC1155(
      "Craate NFT",
      "WINE",
      _uri,
      _proxyRegistryAddress
    )
    public
  {
      maxTokenID = 1;
      batchCount = 0;
  }

  //////////////////////////////////////////////////////////////////////////////

  /*
   *  Create a batch with N bottles.
   */
  function createBatch(
    uint256 maxBottles,
    uint256 ethCost
  )
    external
  {
    require(hasRole(CREATOR_ROLE, _msgSender()), "not a creator");
    require(maxBottles > 0, "bad dims");
    require(ethCost > 0, "bad cost");

    /*
     *  Create tokens to represent each puzzle in this scene.
     *
     *  Each puzzle will have one additional token to track the completed puzzle
     *  that can only be acquired by burning all the subsequent puzzle tiles (1
     *  of each that make up the image).
     */

    // We are about to make a new batch.
    batchCount = batchCount.add(1);

    // Create a batch.
    uint256 batchId = batchCount;

    // Grab an ID and advance the maxTokenID for the base contract.
    maxTokenID  = maxTokenID.add(1);

    // Fill out the storage.
    Batch storage b = batches[batchId];
    b.exists        = true;
    b.numBottles    = 0;
    b.maxBottles    = maxBottles;
    b.ethCost       = ethCost;
  }

  //////////////////////////////////////////////////////////////////////////////

  /*
   *  Public and inter-contract queries for batch info.
   */
  function batchExists(uint256 batchId) view external returns (bool) { return (batches[batchId].exists == true); }
  function batchInfo(uint256 batchId) view external returns (uint256, uint256, uint256) { return (batches[batchId].maxBottles, batches[batchId].numBottles, batches[batchId].ethCost); }

  //////////////////////////////////////////////////////////////////////////////

  function _beforeTokenTransfer
  (
    address /*operator*/,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory /*data*/
  )
    internal
    override
  {
    /*
     *  A new token was minted!
     *
     *  Add hooks for ownership tracking etc here.
     */
    for (uint i = 0; i < ids.length; i++) {
      // Token minted - reduce from supply.
      if (from == address(0)) {
        batches[ids[i]].numBottles = batches[ids[i]].numBottles.add(amounts[i]);
      }

      // Token burn, reduce max count and supply.
      if (to == address(0)) {
        batches[ids[i]].numBottles = batches[ids[i]].numBottles.sub(amounts[i]);
        batches[ids[i]].maxBottles = batches[ids[i]].maxBottles.sub(amounts[i]);
      }
    }
  }
}
