// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

////////////////////////////////////////////////////////////////////////////////

import "./Bottle.sol";

////////////////////////////////////////////////////////////////////////////////

/*
 *  BottleWrapper implements a contract which allows bottles to be purcahsed for
 *  ETH.
 */
contract BottleWrapper is Ownable
{
  using SafeMath for uint256;

  //////////////////////////////////////////////////////////////////////////////

  Bottle public bottleContract;

  //////////////////////////////////////////////////////////////////////////////

  uint256 seed;

  constructor(
    address _bottleAddress
  )
    Ownable()
    public
  {
    bottleContract = Bottle(_bottleAddress);
  }

  //////////////////////////////////////////////////////////////////////////////

  /*
   *  Update contract address for the ESTile contract.
   */
  function setBottleAddress(
    address _address
  )
    external
    onlyOwner
  {
    require(_address != address(0), "Can't set zero address");
    bottleContract = Bottle(_address);
  }

  //////////////////////////////////////////////////////////////////////////////

  function _mintBottleFromBatch(
    address recipient,
    uint256 batchId,
    uint256 count
  )
    internal
  {
    uint256[] memory tokenIdsToMint = new uint256[](1);
    uint256[] memory quantitiesToMint = new uint256[](1);

    tokenIdsToMint[0] = batchId;
    quantitiesToMint[0] = count;

    bottleContract.mintBatch(recipient, tokenIdsToMint, quantitiesToMint, "");
  }

  function buyBottlesForETH(
    uint256 batchId,
    uint256 count
  )
    payable public
  {
    uint256 maxBottles;
    uint256 numBottles;
    uint256 ethCost;
    (maxBottles, numBottles, ethCost) = bottleContract.batchInfo(batchId);
    require(ethCost > 0);
    require(numBottles + count < maxBottles);

    uint256 cost = ethCost.mul(count);
    require(msg.value >= cost, "not enough eth");
    _mintBottleFromBatch(msg.sender, batchId, count);
  }

  function airdropBottles(
    uint256 batchId,
    address[] memory tos,
    uint256 count
  )
    public
    onlyOwner
  {
    require(count > 0);

    uint256[] memory tokenIdsToMint = new uint256[](1);
    uint256[] memory quantitiesToMint = new uint256[](1);

    tokenIdsToMint[0] = batchId;
    quantitiesToMint[0] = count;

    for (uint256 ti = 0; ti < tos.length; ti++) {
      bottleContract.mintBatch(tos[ti], tokenIdsToMint, quantitiesToMint, "");
    }
  }

  //////////////////////////////////////////////////////////////////////////////

  function withdrawBalance()
    public
    onlyOwner
  {
    uint256 balance = address(this).balance;
    require(balance > 0, "no balance left");
    address payable owner = payable(owner());
    owner.transfer(balance);
  }

  uint256 internal nonce;

  function _random(address behalf)
    internal
    returns (uint256)
  {
    nonce++;
    uint256 randomNumber = uint256(
      keccak256(
        abi.encodePacked(
          blockhash(block.number - 1),
          behalf,
          nonce,
          seed
        )
      )
    );
    seed = randomNumber;
    return randomNumber;
  }

  function setSeed(
    uint256 _newSeed
  )
    public
    onlyOwner
  {
    seed = _newSeed;
  }
}
